<?php

namespace AppBundle\Repository;

/**
 * TravailAvantVenteRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TravailAvantVenteRepository extends \Doctrine\ORM\EntityRepository
{

	 public function findAllByDescCharge(){
	 	return 	$this->createQueryBuilder('TravailLivraison')->orderBy('TravailLivraison.charge', 'DESC');
	 }

}
